import React, { useEffect, useRef } from 'react';
import '../styles/projects.css';

function UniProjects() {
  const scrollContainerRef = useRef(null);

  useEffect(() => {
    const handleScroll = (evt) => {
      // Prevent the default scroll behavior
      evt.preventDefault();

      // Scroll the entire page horizontally
      window.scrollBy({
        left: evt.deltaY, // Change this to evt.deltaX if you want vertical scrolling
        behavior: 'smooth', // Add this for smooth scrolling
      });
    };

    // Attach the wheel scroll event listener to the window
    window.addEventListener('wheel', handleScroll);

    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener('wheel', handleScroll);
    };
  }, []);

  return (
    <div id="pageContainer">
      <div ref={scrollContainerRef} className='scrollableObject'>
        {/* Rest of your content */}
      </div>
    </div>
  );
}

export default UniProjects;
